import React, { Component } from 'react';

class AnswerBox extends React.Component {
    constructor() {
        super();
        this.state = {
            answer: '', student: ''
        }
        this.recordAnswer = this.recordAnswer.bind(this);
        this.submitHandler = this.submitHandler.bind(this);
    }

    recordAnswer = (e) => {
        e.preventDefault();
        for (student in this.props.chosenStudents) {
            this.setState({ answer: e.target.value, student: student })
        }
    }

    submitHandler = (e) => {
        e.preventDefault();
        this.props.answerHandler(this.state.answer);
        alert(this.state.answer)
        this.setState({ answer: '', student: '' })
    }

    render() {
        return (
            <div>
                <h1>Hello</h1>
                <div>
                    <input type="text" value={this.state.answer} onChange={this.recordAnswer} />
                </div>
                <div>
                    <button type="submit" onClick={this.submitHandler}>Submit</button>
                </div>
            </div>
        );
    }
};

export default AnswerBox;
