import React, { Component } from 'react';

class PickStudents extends React.Component {
    constructor() {
        super();
        this.state = { student_name: [ 'Oliver', 'Jake', 'Noah', 'James', 'Jack', 'Connor', 'Liam', 'John', 'Harry', 'Callum', 
        'Mason', 'Robert', 'Jacob', 'Michael','Charlie', 'Kyle', 'William', 'Thomas', 'Joe', 'Ethan', 'David', 
        'George', 'Reece', 'Richard'], chosen_student: [] }
    }

    Pop_stu = () => {
        if(this.state.student_name.length == 0) {
            alert("All student have been called once before!")
        }
        for (let i=0; i<4; i++) {
            let idx = Math.floor(Math.random() * this.state.student_name.length);
            let stud = this.state.student_name.splice(idx, 1);
            this.setState({ chosen_student: this.state.chosen_student.concat(stud) });
        }
        this.props.chooseStudent(this.state.chosen_student);
      }

    render() {
        return( 
            <button type="submit" class="btn btn-primar" id="nrp" onClick={this.Pop_stu}>
            Random Student</button>
             );
    }

}

export default PickStudents;
