import React, { Component } from 'react';
import { Input } from 'antd/lib/Input';

const { TextArea } = Input;

class AnswerBox extends React.Component {

    constructor() {
        super();
        this.state = {
            answer: ''
        }
        this.recordAnswer = this.recordAnswer.bind(this);
        this.submitHandler = this.submitHandler.bind(this);
    }

    recordAnswer = (e) => {
        e.preventDefault();
        this.setState({ answer: e.target.value});
    }

    submitHandler = (e) => {
        e.preventDefault();
        this.props.addAnswer(this.state.answer);
        alert("Your answer has been submitted.")
        this.setState({ answer: ''})
    }

    render() {
        return (
            <div>
                <h1>Hello {this.props.student}</h1>
                <div>
                    <TextArea rows={4} type="text"  defaultValue='Input your answer' value={this.state.answer} onChange={this.recordAnswer} />
                </div>
                <div>
                    <button type="submit" className="btn btn-primary" onClick={this.submitHandler}>Submit</button>
                </div>
            </div>
        );
    }
};

export default AnswerBox;
