import React, { Component } from 'react';

class VoteAnswer extends React.Component {
    constructor() {
        super();
        this.addVote = this.addVote.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(content) {
        this.props.answersArray.filter(answerItem => answerItem.answer == content).vote += 1;
        alert(this.props.answersArray.vote);
    }

    addVote = () => {
        let votes = this.props.answersArray.map(
            (answer,i) => {
                return(
                    <li key={i} onClick={() => this.handleClick(answer.answer)}>
                    <button type="submit" className="btn btn-primar">
                        <span>{answer.answer}</span>
                        <span>{answer.vote}</span>
                    </button>
                </li>
                )
            })
            return votes;
        }


    render() {
        return (
            <ul className="list-group">
                {/* {this.props.answersArray.map(
                    function (answer,i) {
                        return <li key={i} onClick={() => this.addVote(answer.answer)}>
                            <button type="submit" className="btn btn-primar">
                                <span>{answer.answer}</span>
                                <span>{answer.vote}</span>
                            </button>
                        </li>
                    })} */}
                {this.addVote()}
            </ul>
        )
    };

}

export default VoteAnswer;
