import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import AnswerBox from './Components/AnswerBox';
import VoteAnswer from './Components/VoteAnswer';
import PickStudent from './Components/PickStudents';

class App extends Component {
  constructor() {
    super();
    this.state = {
      answersArray: [], chosenStudents: []
    }
    this.answerHandler = this.answerHandler.bind(this)
  }

  chooseStudent = (students) => {
    for (let student in students) {
      this.setState({
        answersArray: this.state.answersArray.concat({ student: student, answer: '', vote: 0 }),
        chooseStudent: this.state.chosenStudents.concat(students)
      })
    }
  }

  answerHandler = (answer, student) => {
    let asw_arr = this.state.answersArray.slice();
    asw_arr.filter(studentInfo => studentInfo.student === student).answer = answer;
    this.setState({ answersArray: asw_arr }), function() {alert(this.answersArray)}
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <PickStudent chooseStudent={this.chooseStudent} />
          <div>
            <AnswerBox answersArray={this.state.answersArray} answerHandler={this.answerHandler}
              chosenStudents={this.state.chosenStudents} />
          </div>
          <div>
            <VoteAnswer answersArray={this.state.answersArray} />
          </div>
        </header>
      </div>
    );
  }
}

export default App;
