import React, { Component } from 'react';

class VoteAnswer extends React.Component {
    constructor() {
        super();
        this.addVote = this.addVote.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(idx) {
        this.props.answersArray[idx].vote += 1;
        alert("Thanks for voting!", this.props.answersArray[idx].answer + this.props.answersArray[idx].vote);
        this.forceUpdate();
    }

    addVote = () => {
        let answersArray = this.props.answersArray
        const votes = answersArray.map(
            (i, instance) => {
                return <li key={i} onClick={() => this.handleClick(i)}>
                    <button type="button" class="btn btn-primary">
                        {instance.answer} <span className="badge badge-light">{instance.vote} </span>
                    </button>
                </li>
            })
        return votes;
    }


    render() {
        return (
            <ul className="list-group">
                {this.addVote()}
            </ul>
        )
    };

}

export default VoteAnswer;
