import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import AnswerBox from './Components/AnswerBox';
import VoteAnswer from './Components/VoteAnswer';
import PickStudent from './Components/PickStudents';

class App extends Component {
  constructor() {
    super();
    this.state = {
      student_name: ['Oliver', 'Jake', 'Noah', 'James', 'Jack', 'Connor', 'Liam', 'John', 'Harry', 'Callum',
        'Mason', 'Robert', 'Jacob', 'Michael', 'Charlie', 'Kyle', 'William', 'Thomas', 'Joe', 'Ethan', 'David',
        'George', 'Reece', 'Richard'],
      chosenStudents: [],
    }
    this.handleClick = this.handleClick.bind(this)
    this.answerHandler = this.answerHandler.bind(this)
  }

  getRandomSubarray = (arr, size) => {
    var shuffled = arr.slice(0), i = arr.length, temp, index;
    while (i--) {
      index = Math.floor((i + 1) * Math.random());
      temp = shuffled[index];
      shuffled[index] = shuffled[i];
      shuffled[i] = temp;
    }
    return shuffled.slice(0, size);
  }

  handleClick = (e) => {
    e.preventDefault();
    let studentList = this.getRandomSubarray(this.state.student_name, 4);
    console.log(studentList);
    this.setState({ chosenStudents: studentList });
    let asw_Arr = [];
    for (let i = 0, len = studentList.length; i < len; i++) {
      asw_Arr = asw_Arr.concat({ student: studentList[i], answer: '', vote: 0 })
    }
    this.setState({ answersArray: asw_Arr })
  }

  addAnswer = () => {
    while (this.state.chosenStudents.length > 0) {
      let student = this.state.chosenStudents.pop()
      let answer = this.answerHandler()
      this.setState( {answersArray: this.state.answersArray.filter(
        info=>info.student === student).answer = answer
      } )
    }

  }

  answerHandler(answer) {
    return answer
  }

  handlePage = () => {
    if (this.state.chosenStudents.length === 0 && this.state.answersArray === 0) {
      return (
        <PickStudent chooseStudent={this.handleClick} />
      )
    } else if (this.state.chosenStudents.length === 4 && this.state.answersArray === 4) {
      return (
        <AnswerBox addAnswer={this.addAnswer} 
          chosenStudents={this.state.chosenStudents} answerHandler={this.answerHandler} />
      )
    } else if (this.state.chosenStudents.length === 0 && this.state.answersArray === 4) {
      <VoteAnswer answersArray={this.state.answersArray} />
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div class="alert alert-info" role="alert">
          {this.handlePage()}
          </div>
        </header>
      </div>
    );
  }
}

export default App;
